const body = $("Webhook: Update Config").first().json.body || $("Webhook: Update Config").first().json;
let existing = { topics: [], tickers: [] };

try {
  const binaryData = $input.first().binary?.data;
  if (binaryData) {
    const buffer = Buffer.from(binaryData.data, 'base64');
    existing = JSON.parse(buffer.toString());
  }
} catch (e) {
  // No existing config
}

// Reset functionality
if (body.clear === true || body.reset === true) {
  existing = { topics: [], tickers: [] };
} else {
  // Toggle logic for topics
  if (Array.isArray(body.topics)) {
    body.topics.forEach(t => {
      const index = existing.topics.indexOf(t);
      if (index > -1) existing.topics.splice(index, 1);
      else existing.topics.push(t);
    });
  }

  // Toggle logic for tickers
  if (Array.isArray(body.tickers)) {
    body.tickers.forEach(t => {
      const index = existing.tickers.indexOf(t);
      if (index > -1) existing.tickers.splice(index, 1);
      else existing.tickers.push(t);
    });
  }
}

return {
  binary: {
    data: {
      data: Buffer.from(JSON.stringify(existing)).toString('base64'),
      mimeType: 'application/json',
      fileName: 'briefing_config.json'
    }
  },
  // Return the new state so the user can see what's currently active
  new_config: existing
};

let userConfig = { topics: [], tickers: [] };

try {
  const binaryData = $input.first().binary?.data;
  if (binaryData) {
    const buffer = Buffer.from(binaryData.data, 'base64');
    const parsed = JSON.parse(buffer.toString());
    // Handle both array-of-objects and single object formats
    userConfig = Array.isArray(parsed) ? (parsed[0] || userConfig) : parsed;
  }
} catch (e) {
  // File missing or corrupt, start with empty config
}

const topics = userConfig.topics || [];
const tickers = userConfig.tickers || [];

return {
  topics,
  tickers,
  rssQuery: topics.length > 0 ? topics.join(' OR ') : "market news"
};

const items = $input.all();
return {
  title: items.map(i => i.json.title),
  link: items.map(i => i.json.link),
  content: items.map(i => i.json.content),
  media: items.map(i => i.json.thumbnail || i.json.enclosure?.url || "")
};

// DATA PRE-PROCESSOR
const config = $("Config").first().json;

// Safely get Stocks data
let stocks = [];
try {
  const rawCharts = $("Aggregate Stocks").first().json.chart || [];
  stocks = rawCharts.map(chart => {
    const res = chart.result?.[0];
    if (!res) return null;
    const meta = res.meta;
    const quote = res.indicators?.quote?.[0] || {};
    const close = quote.close?.[0] || meta.regularMarketPrice;
    const prevClose = meta.chartPreviousClose;
    return {
      ticker: meta.symbol,
      name: meta.longName || meta.symbol,
      price: close,
      change: prevClose ? parseFloat(((close - prevClose) / prevClose * 100).toFixed(2)) : 0,
      volume: quote.volume?.[0] || meta.regularMarketVolume,
      ma50: close
    };
  }).filter(s => s !== null);
} catch (e) {
  console.log(\"Stocks data not available\");
}

// Safely get News data
let news = [];
try {
  const newsJson = $(\"Aggregate News\").first().json;
  const titles = newsJson.title || [];
  const links = newsJson.link || [];
  const contents = newsJson.content || [];
  const media = newsJson.media || [];

  news = titles.map((title, i) => ({
    title: Array.isArray(title) ? title[0] : title,
    l: Array.isArray(links[i]) ? links[i][0] : links[i] || \"\",
    takeaway: (Array.isArray(contents[i]) ? contents[i][0] : contents[i] || \"\").replace(/<[^>]*>?/gm, '').slice(0, 500),
    img: Array.isArray(media[i]) ? media[i][0] : media[i] || \"\"
  })).filter(item => item.title);
} catch (e) {
  console.log("News data not available");
}

if (stocks.length === 0 && news.length === 0) {
  throw new Error("No data available from either Stocks or News. Check your configuration.");
}

const suffix = "Return ONLY raw JSON. No talk, no preamble, no tools.";

const getCommand = (prompt, data) => {
  return `cd /home/adam/n8n && cat <<'EOF' | gemini -p "" --yolo\n${prompt} ${suffix}\nInput: ${JSON.stringify(data)}\nEOF`;
};

return {
  stocks,
  news: news.slice(0, 30), // Limit to top 30 items
  cmd_news: getCommand("Act as a News Intelligence Agent. Process news into 3 buckets: 1. Critical Intel, 2. General Pulse, 3. The Bright Side. For each item, you MUST include: 1. original clickable link (l), 2. image URL (img) if available, and 3. a 'sentiment' field (float between -1.0 and 1.0). Scale: -1.0 is extreme Conflict/Negative, 0.0 is Neutral, and 1.0 is extreme Growth/Positive. Prioritize relevance and interesting connections.", news.slice(0, 20)),
  cmd_market: getCommand("Act as a Market Analyst and Technical Expert. Provide analysis of the stock data. For every ticker, include a 'sentiment' field (-1.0 to 1.0).", stocks),
  cmd_macro: getCommand("Act as a Strategic Opportunity Scout. Find the 'Alpha'â€”hidden connections between news and market data. include a 'sentiment' field (-1.0 to 1.0).", { stocks, news: news.slice(0, 10) })
};

const results = {
  agent_results: {
    news_intel: $("Agent: News Intel").first().json,
    market_analyst: $("Agent: Market Analyst").first().json,
    opportunity_scout: $("Agent: Opportunity Scout").first().json
  }
};

const suffix = "Return ONLY raw JSON. No talk, no preamble, no tools.";
const architectPrompt = "Act as the Lead Architect. Cross-correlate findings and format into Alpha-Horizon JSON. Every top-level category (e.g., 'Macro-Market Nexus', 'Israel Geopolitics', 'Pulse of the World') MUST include a 'sentiment_score' (float between -1.0 and 1.0), calculated as the weighted average of the 'sentiment' fields from the items within that category. Scale: -1.0 (Bearish/Conflict/Red), 0.0 (Neutral), 1.0 (Bullish/Growth/Green). Ensure the final JSON structure is perfectly formed and highly actionable.";

const command = `cd /home/adam/n8n && cat <<'EOF' | gemini -p "" --yolo\n${architectPrompt} ${suffix}\nInput: ${JSON.stringify(results)}\nEOF`;

return {
  ...results,
  cmd_architect: command
};

const stdout = $("Agent: Lead Architect").first().json.stdout;

// More robust JSON extraction (handles preamble and markdown code blocks)
const jsonMatch = stdout.match(/\{[\s\S]*\}/) || stdout.match(/\[[\s\S]*\]/);
if (!jsonMatch) throw new Error("No JSON found in agent output");

const cleanJson = jsonMatch[0].trim();

return {
  json: JSON.parse(cleanJson),
  binary: {
    data: {
      data: Buffer.from(cleanJson).toString('base64'),
      mimeType: 'application/json',
      fileName: 'briefing.json'
    }
  }
};
