// Handle both API response format (with .data) and direct file format
const inputData = $input.first().json.data || $input.first().json;

let rawTopics = inputData.topics || [];
const tickers = inputData.tickers || [];

// Extract names from objects if necessary and filter for enabled topics
const topics = rawTopics.map(t => {
  if (typeof t === 'string') return t;
  return t.enabled !== false ? t.name : null;
}).filter(t => t !== null);

return {
  topics,
  tickers,
  rssQuery: topics.length > 0 ? topics.join(' OR ') : "market news"
};

const items = $input.all();
return {
  title: items.map(i => i.json.title),
  link: items.map(i => i.json.link),
  content: items.map(i => i.json.content),
  media: items.map(i => i.json.thumbnail || i.json.enclosure?.url || "")
};

// DATA PRE-PROCESSOR
const config = $("Config").first().json;

// Safely get Stocks data
let stocks = [];
try {
  const stocksData = $("Aggregate Stocks").first().json;
  const rawCharts = Array.isArray(stocksData.chart) ? stocksData.chart : [];
  for (let i = 0; i < rawCharts.length; i++) {
    const chart = rawCharts[i];
    if (!chart) continue;
    const res = chart.result && chart.result[0];
    if (!res) continue;
    const meta = res.meta || {};
    const indicators = res.indicators || {};
    const quote = (indicators.quote && indicators.quote[0]) || {};
    const close = (quote.close && quote.close[0]) || meta.regularMarketPrice;
    const prevClose = meta.chartPreviousClose;
    stocks.push({
      ticker: meta.symbol,
      name: meta.longName || meta.symbol,
      price: close,
      change: prevClose ? parseFloat(((close - prevClose) / prevClose * 100).toFixed(2)) : 0,
      volume: (quote.volume && quote.volume[0]) || meta.regularMarketVolume,
      ma50: close
    });
  }
} catch (e) { console.log('Stocks data processing error', e); }

// Safely get News data
let news = [];
try {
  const titles = $("Aggregate News").first().json.title || [];
  const links = $("Aggregate News").first().json.link || [];
  const contents = $("Aggregate News").first().json.content || [];
  const media = $("Aggregate News").first().json.media || [];

  news = titles.map((title, i) => {
       return {
        title: Array.isArray(title) ? title[0] : title,
        l: Array.isArray(links[i]) ? links[i][0] : (links[i] || ""),
        takeaway: (Array.isArray(contents[i]) ? contents[i][0] : (contents[i] || ""))
                   .replace(/<[^>]*>?/gm, '')
                   .slice(0, 500),
        img: Array.isArray(media[i]) ? media[i][0] : (media[i] || "")
      };
    }).filter(item => item.title && item.title.trim().length > 0);
} catch (e) {
  console.log("News data not available", e);
}

if (stocks.length === 0 && news.length === 0) {
  throw new Error("No data available from either Stocks or News. Check your configuration.");
}

const suffix = "Return ONLY raw JSON. No talk, no preamble, no tools.";

const getCommand = (prompt, data) => {
  return `cd /home/adam/n8n && cat <<'EOF' | gemini -p "" --yolo\n${prompt} ${suffix}\nInput: ${JSON.stringify(data)}\nEOF`;
};

return {
  stocks,
  news: news.slice(0, 50),
  cmd_news: getCommand("Act as a News Intelligence Agent. Analyze and bucket news into: 1. Critical Intel, 2. Market Pulse, 3. Strategic Trends. Include sentiment (-1.0 to 1.0).", news.slice(0, 20)),
  cmd_market: getCommand("Act as a Market Analyst with 20+ years of experience. Provide deep research and analysis for the near future (1-3 months) specifically for the user's watchlist stocks: " + stocks.map(s => s.ticker).join(', ') + ". Focus on catalysts, risks, and potential price action. Include a sentiment score (-1.0 to 1.0) for each.", stocks),
  cmd_macro: getCommand("Act as an Opportunity Scout. Your mission is to hunt for NEW, high-conviction investment opportunities NOT related to the user's current watchlist. Scan market analytics, financial forums, and alternative data sources to find 3-5 assets with high potential over a 1-3 month horizon. For each opportunity, you MUST provide: 'ticker', 'name', a detailed 'explanation' of your reasoning (the 'why'), a 'sentiment' score, and a 'horizon' of '1-3 months'. This is for research, not financial advice.", {})
};

const results = {
  agent_results: {
    news_intel: $("Agent: News Intel").first().json,
    market_analyst: $("Agent: Market Analyst").first().json,
    opportunity_scout: $("Agent: Opportunity Scout").first().json
  }
};

const suffix = "Return ONLY raw JSON. No talk, no preamble, no tools.";
const architectPrompt = "Act as the Lead Architect. Your goal is to produce the final Daily Briefing JSON. Your output MUST follow this structure: 1. Create standard categories for news and market analysis (e.g., 'Market Pulse', 'Watchlist Analysis'). 2. For these standard categories, provide: 'sentiment_score', 'summary', and an 'items' array. NEWS ITEMS must have: 'title', 'l' (link), 'sentiment', 'img', and 'takeaway'. STOCK ITEMS must have: 'ticker', 'name', 'price', 'change', 'sentiment', and 'analysis'. 3. CRITICALLY, you MUST create a top-level category named 'strategic_opportunities'. Items in this category, sourced from the Opportunity Scout, MUST have the following fields: 'ticker', 'name', 'explanation' (the detailed reasoning from the scout), 'sentiment', and 'horizon' (e.g., '1-3 months'). Ensure the final output is ONLY the raw JSON object.";

const command = `cd /home/adam/n8n && cat <<'EOF' | gemini -p "" --yolo\n${architectPrompt} ${suffix}\nInput: ${JSON.stringify(results)}\nEOF`;

return {
  ...results,
  cmd_architect: command
};
